export default function Sketch(p) {
  let size = 0;

  p.setup = function() {
    p.createCanvas(600, 600);
  };

  p.draw = function() {
    p.background(0);
    p.noStroke();
    p.fill(255);
    p.ellipse(p.mouseX, p.mouseY, size, size);

    if (size > 60) {
      size = 0;
    }
    size++;
  };
}
